open class NightCompleteMomentumTheme: MomentumThemeTokensProtocol {
    open var surface: any MomentumThemeSurfaceColorPaletteProtocol
    open var content: any MomentumThemeContentColorPaletteProtocol
    open var overlay: any MomentumThemeOverlayColorPaletteProtocol
    open var border: any MomentumThemeBorderColorPaletteProtocol
    open var bg: any MomentumThemeBgColorPaletteProtocol

    public init(
        surface: any MomentumThemeSurfaceColorPaletteProtocol = NightCompleteMomentumThemeSurfaceColorPalette(),
        content: any MomentumThemeContentColorPaletteProtocol = NightCompleteMomentumThemeContentColorPalette(),
        overlay: any MomentumThemeOverlayColorPaletteProtocol = NightCompleteMomentumThemeOverlayColorPalette(),
        border: any MomentumThemeBorderColorPaletteProtocol = NightCompleteMomentumThemeBorderColorPalette(),
        bg: any MomentumThemeBgColorPaletteProtocol = NightCompleteMomentumThemeBgColorPalette()
    ) {
        self.surface = surface
        self.content = content
        self.overlay = overlay
        self.border = border
        self.bg = bg
    }
